#!/usr/bin/env sh

# Synopsis:
# Test the test runner by running it against a predefined set of solutions 
# with an expected output.

# Output:
# Outputs the diff of the expected test results against the actual test results
# generated by the test runner.

# Example:
# ./bin/run-tests.sh

exit_code=0
# Copy the tests dir to a temp dir, because in the container the user lacks
# permissions to write to the tests dir.
tmp_dir='/tmp/exercism-zig-test-runner'
rm -rf "${tmp_dir}"
mkdir -p "${tmp_dir}"
cp -r tests/* "${tmp_dir}"

# Iterate over all test directories
for test_dir in "${tmp_dir}"/*; do
    test_dir_name=$(basename "${test_dir}")
    test_dir_path=$(realpath "${test_dir}")
    results_file_path="${test_dir_path}/results.json"
    expected_results_file_path="${test_dir_path}/expected_results.json"

    bin/run.sh "${test_dir_name}" "${test_dir_path}" "${test_dir_path}"

    for file in "$results_file_path" "$expected_results_file_path"; do
        # We remove nondeterministic memory locations of instructions.
        # See: https://github.com/exercism/zig-test-runner/issues/26
        sed -E 's/0x[a-f0-9]{6}/<MEMHASH>/g' "${file}" > "${file}.tmp"
    done

    echo "${test_dir_name}: comparing results.json to expected_results.json"

    if ! diff "${results_file_path}.tmp" "${expected_results_file_path}.tmp"; then
        exit_code=1
    fi
done

rm -rf "${tmp_dir}"
exit ${exit_code}
